@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: 210 11% 98%; /* #F8FAFC */
  --foreground: 215 25% 27%; /* #374151 */
  --muted: 210 40% 96%; /* #F1F5F9 */
  --muted-foreground: 215 16% 47%; /* #64748B */
  --popover: 0 0% 100%; /* #FFFFFF */
  --popover-foreground: 215 25% 27%; /* #374151 */
  --card: 0 0% 100%; /* #FFFFFF */
  --card-foreground: 215 25% 27%; /* #374151 */
  --border: 214 32% 91%; /* #E2E8F0 */
  --input: 214 32% 91%; /* #E2E8F0 */
  --primary: 217 91% 60%; /* #3B82F6 */
  --primary-foreground: 0 0% 100%; /* #FFFFFF */
  --secondary: 210 40% 96%; /* #F1F5F9 */
  --secondary-foreground: 215 25% 27%; /* #374151 */
  --accent: 210 40% 96%; /* #F1F5F9 */
  --accent-foreground: 215 25% 27%; /* #374151 */
  --destructive: 0 84% 60%; /* #EF4444 */
  --destructive-foreground: 0 0% 100%; /* #FFFFFF */
  --ring: 217 91% 60%; /* #3B82F6 */
  --radius: 0.5rem;

  /* Custom Swiss brand colors */
  --swiss-blue: 224 76% 48%; /* #1E3A8A */
  --swiss-slate: 215 16% 47%; /* #64748B */
  --swiss-light: 210 11% 98%; /* #F8FAFC */
}

.dark {
  --background: 222 84% 5%; /* #0F172A */
  --foreground: 210 40% 98%; /* #F8FAFC */
  --muted: 217 33% 17%; /* #1E293B */
  --muted-foreground: 215 20% 65%; /* #94A3B8 */
  --popover: 222 84% 5%; /* #0F172A */
  --popover-foreground: 210 40% 98%; /* #F8FAFC */
  --card: 222 84% 5%; /* #0F172A */
  --card-foreground: 210 40% 98%; /* #F8FAFC */
  --border: 217 33% 17%; /* #1E293B */
  --input: 217 33% 17%; /* #1E293B */
  --primary: 217 91% 60%; /* #3B82F6 */
  --primary-foreground: 222 84% 5%; /* #0F172A */
  --secondary: 217 33% 17%; /* #1E293B */
  --secondary-foreground: 210 40% 98%; /* #F8FAFC */
  --accent: 217 33% 17%; /* #1E293B */
  --accent-foreground: 210 40% 98%; /* #F8FAFC */
  --destructive: 0 63% 31%; /* #991B1B */
  --destructive-foreground: 210 40% 98%; /* #F8FAFC */
  --ring: 217 91% 60%; /* #3B82F6 */
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-sans antialiased;
    font-family: 'Inter', system-ui, -apple-system, sans-serif;
  }
}

@layer components {
  .step-transition {
    @apply transition-all duration-300 ease-in-out;
  }

  .card-hover {
    @apply transition-all duration-300 hover:shadow-lg hover:scale-[1.02];
  }

  .swiss-blue {
    color: hsl(var(--swiss-blue));
  }

  .bg-swiss-blue {
    background-color: hsl(var(--swiss-blue));
  }

  .border-swiss-blue {
    border-color: hsl(var(--swiss-blue));
  }

  .text-swiss-slate {
    color: hsl(var(--swiss-slate));
  }

  .bg-swiss-light {
    background-color: hsl(var(--swiss-light));
  }
}

@layer utilities {
  .animate-fade-in {
    animation: fadeIn 0.5s ease-in-out;
  }

  .animate-slide-in {
    animation: slideIn 0.3s ease-out;
  }
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideIn {
  from {
    opacity: 0;
    transform: translateX(-20px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

